let serial;
let state = 0;
let foo;

function setup() {
    createCanvas(400, 400);
    serial = new p5.SerialPort();
    serial.list();
    serial.open('COM3'); // change to the port of your Arduino board
    serial.on('data', serialEvent);
    foo = new p5.SpeechRec();
    foo.onResult = showResult;
    foo.start();
}

function draw() {
    background(220);
    if (state == 0) {
        fill(255, 0, 0);
    } else {
        fill(0, 255, 0);
    }
    ellipse(width/2, height/2, 100, 100);
}

function serialEvent() {
    let data = serial.readLine();
    if (data) {
        data = data.trim();
        if (data == 'on') {
            state = 1;
            serial.write('1');
        } else if (data == 'off') {
            state = 0;
            serial.write('0');
        }
    }
}

function showResult() {
    if (foo.resultValue == true) {
        let input = foo.resultString.toLowerCase();
        if (input == 'on') {
            state = 1;
            serial.write('1');
        } else if (input == 'off') {
            state = 0;
            serial.write('0');
        }
    }
}

