/* ===
ml5 Example
Webcam Image Classification using a pre-trained customized model and p5.js
This example uses p5 preload function to create the classifier
=== */

let serial;
let latestData = "waiting for data";
let classifier; // Classifier Variable
let imageModelURL = 'https://teachablemachine.withgoogle.com/models/l1osovygX/'; // Model URL
let video; // Video
let flippedVideo;
let label = ""; // To store the classification
let audio;
let check;

// Load the model first
function preload() {
  classifier = ml5.imageClassifier(imageModelURL + 'model.json');
  audio = loadSound("Alarm.m4a");
}


function setup() {
  createCanvas(480, 390);
  serial = new p5.SerialPort();
  serial.open("/dev/cu.usbmodem14201");
  serial.on('data', gotData);

  video = createCapture(VIDEO); // Create the video
  video.size(320, 240);
  video.hide();

  flippedVideo = ml5.flipImage(video)
  classifyVideo(); // Start classifying
}

function draw() {
  background(0);
  // Draw the video
  image(flippedVideo, 0, 0, 480, 360);

  // Draw the label
  if (label == "Safe") {
    fill(255, 255, 0);
    audio.stop();
  }
  if (label == "Intruder") {
    fill(0, 255, 0);
    image(check, 350, 70, 108, 132);
    audio.stop();
  }
  if (label == "Warning! Warning!") {
    fill(255, 0, 0);
    if (audio.isPlaying() == false) {
      audio.play();
    }
  }
  textSize(20);
  textAlign(CENTER);
  text(label, width / 2, height - 8);
}



// Get a prediction for the current video frame
function classifyVideo() {
  flippedVideo = ml5.flipImage(video)
  classifier.classify(flippedVideo, gotResult);
}

// When we get a result
function gotResult(error, results) {
  // If there is an error
  if (error) {
    console.error(error);
    return;
  }
  // The results are in an array ordered by confidence.
  //console.log(results[0]);
  label = results[0].label;
  // Classifiy again!
  classifyVideo();

  if (label == "Safe") {
    serial.write("3")
  }

  if (label == "Intruder") {
    serial.write("2")
  }

  if (label == "Warning! Warning!") {
    serial.write("1")
  }
}

function gotData() {
  var currentString = serial.readLine();
  trim(currentString);
  if (!currentString) return;
  latestData = int(currentString);
