let tolerance = 20;
let colorToMatch;
let video;
let positions = [];

function setup() {
  createCanvas(windowWidth, windowHeight);

  video = createCapture(VIDEO);
  video.size(640, 480);
  video.hide();

  colorToMatch = color(140, 190, 255);
}

function draw() {
  image(video, 0, 0);

  let newPositions = findColors(video, colorToMatch, tolerance);
  if (newPositions.length > 0) {
    positions.push(...newPositions);
  }

  noStroke();
  fill(colorToMatch);
  for (let i = 0; i < positions.length; i++) {
    let pos = positions[i];
    ellipse(pos.x, pos.y, 30, 30);
  }

  // Remove positions that are off screen or have lived for too long
  positions = positions.filter(pos => pos.x >= 0 && pos.x < width && pos.y >= 0 && pos.y < height && pos.lifespan > 0);
  positions.forEach(pos => pos.lifespan--);
}

function findColors(input, c, tolerance) {
  let matchR = red(c);
  let matchG = green(c);
  let matchB = blue(c);

  input.loadPixels();
  let newPositions = [];
  for (let y = 0; y < input.height; y++) {
    for (let x = 0; x < input.width; x++) {
      let index = (y * input.width + x) * 4;
      let r = input.pixels[index];
      let g = input.pixels[index + 1];
      let b = input.pixels[index + 2];

      if (r >= matchR - tolerance && r <= matchR + tolerance &&
        g >= matchG - tolerance && g <= matchG + tolerance &&
        b >= matchB - tolerance && b <= matchB + tolerance) {
        newPositions.push({ x, y, lifespan: 60 });
      }
    }
  }
  return newPositions;
}

function mousePressed() {
  loadPixels();
  colorToMatch = get(mouseX, mouseY);
}

