class Fish {
  constructor(x, y, w, h) {
    this.x = x;
    this.y = y;
    this.w = w;
    this.h = h;
    this.r = random(255);
    this.g = random(255);
    this.b = random(255);
    this.xdir = random(-4, 4);
    this.ydir = random(-1, 1);
    this.angle = 0;
  }

  display() {
    fill(this.r, this.g, this.b);
    noStroke();
    ellipse(this.x, this.y, this.w, this.h);
    if (this.xdir < 0) {
      triangle(
        this.x + 30,
        this.y,
        this.x + 80,
        this.y - 30,
        this.x + 80,
        this.y + 30
      );
    } else
      triangle(
        this.x - this.w / 2,
        this.y + this.h / 2,
        this.x - this.w / 2,
        this.y - this.h / 2,
        this.x - this.w / 8,
