            let a = videoimg.pixels[index + 3];
            newRed = r + 0.769*g + 0.189*b;
          newGreen = 0.5*r + 0.9*g;
          newBlue = 0.272*r + 0.534*g + 0.38*b;
            videoimg.pixels[index + 0] = newRed;
            videoimg.pixels[index + 1] = newGreen;
            videoimg.pixels[index + 2] = newBlue;
