//defining everything
let serial;
let latestData = "waiting for data";
let classifier; // Classifier Variable
let imageModelURL = "https://teachablemachine.withgoogle.com/models/-cIRl47R0/"; // Model URL (linking to teachable machine)
let video; // Video
let flippedVideo;
let label = ""; // To store the classification
let audio; //Voldemort audio
let audio1;//Harry audio 

// Load the model first
function preload() {
  classifier = ml5.imageClassifier(imageModelURL + "model.json");
  audio = loadSound("Voldemort.mp3");
  audio1 = loadSound("harry potter.mp3")
}

//Making sure that the server is connected 
function serverConnected() {
  console.log('connected to server.');
}



function setup() {
  createCanvas(480, 390);
  //connecting the serial from p5js to arduino
  serial = new p5.SerialPort();
  serial.open("/dev/tty.usbmodem14201");//connecting arduino port to p5js
  serial.on("data", gotData);
  serial.on('connected', serverConnected); // callback for connecting to the server
  serial.list();                      // list the serial ports

  // Create the video
  video = createCapture(VIDEO); 
  video.size(320, 240);
  video.hide();

  flippedVideo = ml5.flipImage(video);
  classifyVideo(); // Start classifying
}

// make a serial port selector object:
//connecting the right port 
function printList(portList) {
  // create a select object:
  portSelector = createSelect();
  portSelector.position(10, 10);
  // portList is an array of serial port names
  for (var i = 0; i < portList.length; i++) {
    // add this port name to the select object:
    portSelector.option(portList[i]);
  }
  // set an event listener for when the port is changed:
  //portSelector.changed(mySelectEvent);
}

function draw() {
  background(0);
  // Draw the video
  image(flippedVideo, 0, 0, 480, 360);

  // Draw the label
  if (label == "Safe") {
    fill(255, 255, 0);
    //playing the audio if prints "safe"
    audio.stop();
    audio1.stop();
  }
  if (label == "Fight") {
    fill(0, 255, 0);
    //playing the audio if prints "Fight"
    audio.stop();
    audio1.play();
  }
  if (label == "Warning! Warning!") {
    fill(255, 0, 0);
    if (audio.isPlaying() == false) {
      //playing the audio if prints "Warning! Warning!"
      audio.play();
      audio1.stop();
    }
  }
  //font for the texts
  textSize(40);
  textFont('Rampart One');
  textAlign(CENTER);
  text(label, width / 2, height - 8);
}

// Get a prediction for the current video frame
function classifyVideo() {
  flippedVideo = ml5.flipImage(video);
  classifier.classify(flippedVideo, gotResult);
}

// When we get a result
function gotResult(error, results) {
  // If there is an error
  if (error) {
    console.error(error);
    return;
  }
  // The results are in an array ordered by confidence.
  //console.log(results[0]);
  label = results[0].label;
  // Classifiy again!
  classifyVideo();

  if (label == "Safe") {
    //number is the corresponding byte from arduino
    serial.write("3");
  }

  if (label == "Fight") {
    //number is the corresponding byte from arduino
    serial.write("2");
  }

  if (label == "Warning! Warning!") {
    //number is the corresponding byte from arduino
    serial.write("1");
  }
}

//function converts the data values to integer
function gotData() {
  var currentString = serial.readLine();
  trim(currentString);
  if (!currentString) return;
  latestData = int(currentString);
}

