let threshold = 20;

function setup() {
  createCanvas(640,480);

  capture = createCapture({
    video: {
      facingMode: "environment",
      maxWidth: width,
      maxHeight: height
    },
  });
  capture.size(width, height);
  videoBuffer = createGraphics(capture.width, capture.height);
  capture.hide();
  background(0,0,0,255);
}

function draw() {
  videoBuffer.image(capture, 0, 0, width, width * capture.height / capture.width);
  videoBuffer.loadPixels();
  loadPixels();

  for (let y = 0; y < height; y+=1) {
    for (let x = 0; x < width; x+=1) {
      let pos = (width*y+x)*4;
      let r = videoBuffer.pixels[pos];
      let g = videoBuffer.pixels[pos+1];
      let b = videoBuffer.pixels[pos+2];
      let brightness = (r+g+b)/3; // calculate brightness

      // check if the pixel is above the threshold
      if (brightness > threshold) {
        pixels[pos+0] = (videoBuffer.pixels[pos+0] > pixels[pos+0]) ? videoBuffer.pixels[pos+0] : pixels[pos+0];
        pixels[pos+1] = (videoBuffer.pixels[pos+1] > pixels[pos+1]) ? videoBuffer.pixels[pos+1] : pixels[pos+1];
        pixels[pos+2] = (videoBuffer.pixels[pos+2] > pixels[pos+2]) ? videoBuffer.pixels[pos+2] : pixels[pos+2];
      }
    }
  }
  updatePixels();
}

