  } else if (screen == 1) {
    playScreen();
  } else if (screen == 2) {
    endScreen();
  }
}

function startScreen() {
  background(240, 215, 250);

  //Two rows of colored balls
  strokeWeight(2);
  stroke(255);
  for (var x = 0; x <= width; x += 50) {
    fill(random(255), random(255), random(255));
    ellipse(x, 40, 25, 25);
