  text("!!! Monster Eating Game !!!", width / 2, height / 2);
  textSize(16);
  textStyle(NORMAL);
  text("click to start", width / 2, height / 2 + 50);
  fill(255);

  //When the game restarts Initialize the operation
  reset();
}

function playScreen() {
  background(0);

  //Countdown
  textSize(16);
  text(timer, width / 2 + 40, 47);
  textSize(16);
  text("Time remainingï¼š", width / 2 - 40, 47);
  if (frameCount % 60 == 0 && timer > 0) {
    timer--;
  }

      if(timer==0){
       endScreen();
    }

  //score
  textSize(16);
  textStyle(NORMAL);
  text("score = " + score, 68, 47);

  //Ball first drop location
  ellipse(x, y, 20, 20);

  //Little Monsters
  strokeWeight();
  stroke(0);
  fill(250, 0, 0);
  arc(mouseX, height - 10, 80, 60, 100, PI + QUARTER_PI, PIE);
  fill(255);
  circle(mouseX - 22, height - 16, 15);
  fill(0);
  circle(mouseX - 22, height - 19, 10);

