  noStroke();

bezier(mouseX, height-10, mouseX+15, height-60, mouseX+30, height-45, mouseX+20, height-35);

   fill(255);
  noStroke();

  //The ball is lower than the position where it was eaten and it is considered a failure
  y += speed;
  if (y > height) {
    screen = 2;
  }

  //If the ball is caught,
  if (y > height - 10 && x > mouseX - 30 && x < mouseX + 30) {
    y = -20;
    speed += 0.5;
    score += 1;
  }

  //select the location where the next ball appears
  if (y == -20) {
    nextRandom();
  }
}
