  video.size(width, height);

  // Create a new poseNet method with a single detection
  poseNet = ml5.poseNet(video, modelReady);
  // This sets up an event that fills the global variable "poses"
  // with an array every time new poses are detected
  poseNet.on('pose', function(results) {
    poses = results;
    //print(poses);

    if (poses.length > 0) {
      // if (poses[0].pose.leftEar.confidence < 0.5) {
        //drawing.push({x: poses[0].pose.nose.x, y: poses[0].pose.nose.y});
        x = poses[0].pose.nose.x;
        y = poses[0].pose.nose.y;
        //ellipse(poses[0].pose.nose.x, poses[0].pose.nose.y, 20);  
      // }
    }
    //print(poses);
  });
  // Hide the video element, and just show the canvas
  video.hide();

  fill(200,0,0);
  noStroke();
}

function modelReady() {
  print("Model Ready");
  //select('#status').html('Model Loaded');
}

function draw() {
  image(video, 0, 0, width, height);

  // for (let i = 0; i < drawing.length; i++) {
  //   ellipse(drawing[i].x, drawing[i].y, 20);  
