let capture;
let targetColor = [0, 0, 255]; // Set the target color to blue

function setup() {
  createCanvas(640, 480);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide(); // Hide the video element
}

function draw() {
  background(0);
  image(capture, 0, 0, width, height);

  // Use trackColor() to track the blue light wand
  let trackingResult = trackColor(targetColor);

  // Use getTargetColor() to set the target color to blue
  targetColor = getTargetColor();

  // Use findContours() to find the shape of the tracked color
  let contours = findContours(trackingResult, 10, width * height / 4, 10, true);

  // Draw the contours on the canvas
  stroke(0, 255, 0);
  strokeWeight(2);
  noFill();
  for (let i = 0; i < contours.length; i++) {
    beginShape();
    for (let j = 0; j < contours[i].length; j++) {
      vertex(contours[i][j].x, contours[i][j].y);
    }
    endShape(CLOSE);
  }
}

