  let matchG = green(c);
  let matchB = blue(c);

  input.loadPixels();
  for (let y = 0; y < input.height; y++) {
    for (let x = 0; x < input.width; x++) {
      let index = (y * input.width + x) * 4;
      let r = input.pixels[index];
      let g = input.pixels[index + 1];
      let b = input.pixels[index + 2];

      if (r >= matchR - tolerance && r <= matchR + tolerance &&
          g >= matchG - tolerance && g <= matchG + tolerance &&
          b >= matchB - tolerance && b <= matchB + tolerance) {
        return createVector(x, y);
      }
    }
  }
}

// check if the movement is swish and flick
function checkMovement(trail, movement) {
  let start = trail[0];
  let end = trail[trail.length-1];

  // calculate the direction vector of the trail
  let direction = createVector(end.x - start.x, end.y - start.y);

  // calculate the angle between the direction vector and a reference vector pointing to the right
  let angle = direction.heading() - HALF_PI;
  angle = (angle < 0) ? TWO_PI + angle : angle;

  // check if the angle falls within the swish and flick range
if (movement == "swish and flick") {
  if (trail.length < 20) {
      return false;
    }

    // fit a circle to the trail using least squares
    let n = trail.length;
    let sx = 0, sy = 0, sxx = 0, syy = 0, sxy = 0;
    for (let i = 0; i < n; i++) {
      let p = trail[i];
      let x = p.x;
      let y = p.y;
      sx += x;
      sy += y;
      sxx += x * x;
      syy += y * y;
      sxy += x * y;
    }
    let a = (n * sxx - sx * sx);
    let b = (n * sxy - sx * sy);
    let c = (n * syy - sy * sy);
    let d = (a * c - b * b);
    let cx = (c * sx - b * sy) / d;
    let cy = (a * sy - b * sx) / d;
    let r = sqrt((sxx + syy) / n - (cx * cx + cy * cy));

    // calculate the average distance between the points and the circle
    let error = 0;
    for (let i = 0; i < n; i++) {
      let p = trail[i];
      let x = p.x;
      let y = p.y;
      let d = dist(x, y, cx, cy);
      error += abs(d - r);
    }
    error /= n;

    // if the error is within a certain range, return true
    if (error < 5) {
      return true;
    } else {
      return false;
    }

} else if (movement == "zig-zag") {
   let minAngleChange = PI/4; // the direction vector needs to change by at least 45 degrees
  let maxTimeBetweenChanges = 100; // the direction vector can only change direction once every 100 frames

  let timeSinceLastChange = 0;
  let lastAngle = angle;

  // iterate over the trail in reverse order, starting from the second-to-last point
  for (let i = trail.length-2; i >= 0; i--) {
    let p = trail[i];
    let d = createVector(end.x - p.x, end.y - p.y);
    let a = d.heading();

    // calculate the change in angle since the last point
    let angleChange = abs(a - lastAngle);
    angleChange = (angleChange > PI) ? TWO_PI - angleChange : angleChange;

    // if the angle change is large enough and enough time has elapsed, return true
    if (angleChange > minAngleChange && timeSinceLastChange > maxTimeBetweenChanges) {
      return true;
    }

    timeSinceLastChange++;
    lastAngle = a;
  }

  return false;
}
}

