let video;
let colorToMatch;
let ellipsePos;
let buttonPos;
let gameState;

function preload() {
  // load any necessary assets here
  spellSound = loadSound('wingardium_leviosa.mp3');
}

function setup() {
  createCanvas(windowWidth, windowHeight);
  video = createCapture(VIDEO);
  video.size(640, 480);
  video.hide();

  colorToMatch = color(140, 190, 255);
  ellipsePos = createVector(width/2, height/2);
  buttonPos = createVector(width/2, height/2 + 100);

  gameState = 'title';
}

function draw() {
  background(0);

  if (gameState === 'title') {
    titleScreen();
  } else if (gameState === 'game') {
    gameScreen();
  }
}

function titleScreen() {
  image(video, 0, 0);

  // check if blue color is detected
  let bluePos = findColor(colorToMatch);
  if (bluePos) {
    ellipsePos = bluePos;

    // check if ellipse intersects with button
    if (dist(ellipsePos.x, ellipsePos.y, buttonPos.x, buttonPos.y) < 50) {
      gameState = 'game';
    }
  }

  // draw button
  fill(255);
  ellipse(buttonPos.x, buttonPos.y, 100, 50);
  textSize(24);
  textAlign(CENTER, CENTER);
  fill(0);
  text('Start', buttonPos.x, buttonPos.y);

  // draw following ellipse
  noStroke();
  fill(colorToMatch);
  ellipse(ellipsePos.x, ellipsePos.y, 50, 50);
}

function gameScreen() {
  image(video, 0, 0);

  let tolerance = 10;
  let trail = [];
  let spellSound; // sound asset for spell casting

  // code for game goes here
  let newPoint = findColor(colorToMatch);
  if (newPoint !== null) {
    trail.push(newPoint);

    // play the spell sound when the trail length is greater than a threshold and the movement is swish and flick
    if (trail.length > 20 && checkMovement(trail, "swish and flick")) {
      spellSound.play();
    }
  }

  noStroke();
  fill('#EEDD82');
  for (let i = 0; i < trail.length; i++) {
    let p = trail[i];
    ellipse(p.x, p.y, i, i);
  }
  if (trail.length > 20) {
    trail.splice(0,1);
  }
}

function findColor(c) {
  video.loadPixels();
  let tolerance = 10;
  let matchR = red(c);
  let matchG = green(c);
  let matchB = blue(c);

  for (let y = 0; y < video.height; y++) {
    for (let x = 0; x < video.width; x++) {
      let index = (y * video.width + x) * 4;
      let r = video.pixels[index];
      let g = video.pixels[index + 1];
      let b = video.pixels[index + 2];

      if (r >= matchR - tolerance && r <= matchR + tolerance &&
          g >= matchG - tolerance && g <= matchG + tolerance &&
          b >= matchB - tolerance && b <= matchB + tolerance) {
        return createVector(x, y);
      }
    }
  }

  return null;
}

function checkMovement(trail, movement) {
  let start = trail[0];
  let end = trail[trail.length-1];

  // calculate the direction vector of the trail
  let direction = createVector(end.x - start.x, end.y - start.y);

  // calculate the angle between the direction vector and a reference vector pointing to the right
  let angle = direction.heading() - HALF_PI;
  angle = (angle < 0) ? TWO_PI + angle : angle;

  // check if the angle falls within the swish and flick range
  if (angle > PI/4 && angle < 3*PI/4) {
    return true;
  } else {
    return false;
  }
}

