  ellipse(leftKneeX,leftKneeY,30,30);
  ellipse(rightKneeX, rightKneeY,30,30)


     if (leftKneeX <=230)  {
       fill(255)
      textSize(60);
    textAlign(CENTER, TOP);
    text('Correct!',0, 12, width)
  }

  //Countdown
  fill(225,225,225)
  textSize(20);
  textAlign(LEFT)
  text(timer, 190,300);
  text("Time remainingï¼š",30,300);
  if (frameCount % 60 == 0 && timer > 0) {
    timer--;
  }
      if(timer==0){
       drawExit();
    }

  // if (mouseIsPressed) {
  //   state = 2;
  // }
}

