  }


  //Countdown
  fill(225,225,225)
  textSize(20);
  textAlign(LEFT)
  text(timer, 190,300);
  text("Time remaining：",30,300);
  if (frameCount % 60 == 0 && timer > 0) {
    timer--;
  }
      if(timer==0){
       drawExit();
    }

  // if (mouseIsPressed) {
  //   state = 2;
  // }
}

// End Game Screen
